// ==UserScript==
// @name         TWB OBS Web Cleanup
// @icon         data:image/png;base64,
// @namespace    https://github.com/upamanyudas/violetmonky-scripts
// @version      0.2
// @description  Remove unwanted buttons/elements from OBS Web and add a responsive floating window overlay
// @match        https://obs-web.niek.tv/*
// @author       Upamanyu Das
// @grant        none
// @homepage     https://github.com/upamanyudas/violetmonky-scripts
// ==/UserScript==

(function() {
    'use strict';

    // Configurable list of match phrases/symbols
    const removalPhrases = [
        "⌘", "⇊"
    ];

    // Which element types and attributes to check
    const elementConfigs = [
        {selector: "button", attr: "title"},  // buttons by title
        // {selector: "div", attr: "title"},     // divs by title
        // {selector: "span", attr: "title"},    // spans by title
        // Add more rules here if needed
    ];

    // Helper: check if text matches removal phrases
    function shouldRemove(text) {
        return removalPhrases.some(phrase => text && text.includes(phrase));
    }

    // Main removal function
    function removeUnwantedElements() {
        elementConfigs.forEach(config => {
            document.querySelectorAll(config.selector).forEach(el => {
                const text = el.getAttribute(config.attr);
                if (shouldRemove(text)) {
                    // If element is inside <li>, remove the li; otherwise remove element itself
                    const liParent = el.closest("li");
                    if (liParent) {
                        liParent.remove();
                        console.log(`Removed <li> with ${config.selector}[${config.attr}*="${text}"]`);
                    } else {
                        el.remove();
                        console.log(`Removed ${config.selector}[${config.attr}*="${text}"]`);
                    }
                }
            });
        });
    }

    // Create floating window overlay
    function createFloatingWindow() {
        // Create container div
        const overlay = document.createElement('div');
        overlay.id = 'floating-window';
        overlay.style.cssText = `
            position: fixed;
            bottom: 20px;
            right: 20px;
            width: 250px;
            height: 144px;
            background: white;
            border: 1px solid #ccc;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0,0,0,0.2);
            z-index: 10000;
            overflow: hidden;
            resize: both;
            min-width: 200px;
            min-height: 150px;
            max-width: 90vw;
            max-height: 90vh;
        `;

        // Create header for dragging and closing
        const header = document.createElement('div');
        header.style.cssText = `
            background: #f1f1f1;
            padding: 5px;
            cursor: move;
            display: flex;
            justify-content: space-between;
            align-items: center;
            border-bottom: 1px solid #ccc;
        `;
        header.innerHTML = `<span style="padding-left: 10px;">TWB OBS Soundboard</span>`;

        // Create close button
        const closeBtn = document.createElement('button');
        closeBtn.innerText = '×';
        closeBtn.style.cssText = `
            background: none;
            border: none;
            font-size: 16px;
            cursor: pointer;
            padding: 0 10px;
        `;
        closeBtn.onclick = () => overlay.remove();
        header.appendChild(closeBtn);

        // Create iframe for the external webpage
        const iframe = document.createElement('iframe');
        iframe.src = 'https://twb-obs-sfx.dmg.li/';
        iframe.style.cssText = `
            width: 100%;
            height: calc(100% - 30px);
            border: none;
        `;

        // Append elements
        overlay.appendChild(header);
        overlay.appendChild(iframe);
        document.body.appendChild(overlay);

        // Make the window draggable
        let isDragging = false;
        let currentX;
        let currentY;
        let initialX;
        let initialY;

        header.addEventListener('mousedown', (e) => {
            initialX = e.clientX - currentX;
            initialY = e.clientY - currentY;
            isDragging = true;
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                e.preventDefault();
                currentX = e.clientX - initialX;
                currentY = e.clientY - initialY;
                overlay.style.left = `${currentX}px`;
                overlay.style.top = `${currentY}px`;
                overlay.style.right = 'auto';
                overlay.style.bottom = 'auto';
            }
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        // Function to reposition overlay to bottom-right corner
        function repositionOverlay() {
            if (!isDragging) {
                overlay.style.left = 'auto';
                overlay.style.top = 'auto';
                overlay.style.right = '20px';
                overlay.style.bottom = '20px';
                currentX = window.innerWidth - overlay.offsetWidth - 20;
                currentY = window.innerHeight - overlay.offsetHeight - 20;
            }
        }

        // Initialize position
        repositionOverlay();

        // Reposition on window resize
        window.addEventListener('resize', repositionOverlay);
    }

    // Wait until "Disconnect" button appears, then run cleanup and create overlay
    function waitForDisconnectButton() {
        const btn = document.querySelector('button[title="Disconnect"]');
        if (btn) {
            console.log("Disconnect button found — running cleanup and creating overlay");
            removeUnwantedElements();
            createFloatingWindow();
        } else {
            setTimeout(waitForDisconnectButton, 1000);
        }
    }

    waitForDisconnectButton();

})();
